import './editor.css';
import SyntaxHighlighter from "react-syntax-highlighter";
import "./highlighter.css";

export const Editor = ({ placeHolder, onChange, onKeyDown, language, theme, children }) => {
    return (
      <div>
      <textarea
        className="editor"
        placeholder={placeHolder}
        onChange={onChange}
      ></textarea>
      <SyntaxHighlighter
        language={language}
        style={theme}
        className="highlighter"
      >
        {children}
      </SyntaxHighlighter>
      </div>
    );
  };
